(876, 213, '_form', '<div class=\"row\">\n <div class=\"col-12 col-sm-12 col-md-6 col-lg-6 col-xl-6 form-col\">\n   <div class=\"vacantion-block\">\n     <div class=\"form-item vacantion-item\">\n        <label class=\"label\" for=\"#name\">Введите ФИО</label>\n        [text* user_name id:name class:input class:w100 placeholder \"Иванов Иван Иванович\"]\n     </div>\n     <div class=\"form-item vacantion-item\">\n        <label class=\"label\" for=\"#specialty\">Укажите специальность</label>\n        [text* specialty id:specialty class:input class:w100 placeholder \"Специальность\"]\n     </div>\n   </div>\n </div>\n\n <div class=\"col-12 col-sm-12 col-md-6 col-lg-6 col-xl-6 form-col\">\n   <div class=\"vacantion-block\">\n     <div class=\"form-item vacantion-item\">\n       <label class=\"label\" for=\"#phone\">Введите номер телефона</label>\n       [tel* phone id:phone class:input class:w100 class:input-phone placeholder minlength:16 \"Введите номер телефона\"]\n     </div>\n     <div class=\"form-item vacantion-item\">\n       <label class=\"label\" for=\"#citizenship\">Ваше гражданство</label>\n       [text* citizenship id:citizenship class:input class:w100 placeholder \"РФ\"]\n     </div>\n    </div>\n  </div>\n</div>\n[submit class:submit class:mb class:w100 \"Стать мастером\"]\n<div class=\"form-item flex\">\n [checkbox* policy class:label class:mb-0 class:ml use_label_element exclusive \"Я согласен политикой конфиденциальности\"]\n</div>'),
